package com.hotel.home.admin;

public class BedVO {
	
	private int bed_code;
	private String bed_type;
	private int bed_rate;
	
	public BedVO() {
		// TODO Auto-generated constructor stub
	}

	public int getBed_code() {
		return bed_code;
	}

	public void setBed_code(int bed_code) {
		this.bed_code = bed_code;
	}

	public String getBed_type() {
		return bed_type;
	}

	public void setBed_type(String bed_type) {
		this.bed_type = bed_type;
	}

	public int getBed_rate() {
		return bed_rate;
	}

	public void setBed_rate(int bed_rate) {
		this.bed_rate = bed_rate;
	}

}
